import 'package:flutter/material.dart';

@immutable
class AppTextFieldTheme extends ThemeExtension<AppTextFieldTheme> {
  final Color backgroundColor;
  final Color highLightBackgroundColor;
  final Color borderColor;
  final Color focusedBorderColor;

  const AppTextFieldTheme({
    required this.backgroundColor,
    required this.highLightBackgroundColor,
    required this.borderColor,
    required this.focusedBorderColor,
  });

  @override
  AppTextFieldTheme copyWith({
    Color? backgroundColor,
    Color? highLightBackgroundColor,
    Color? borderColor,
    Color? focusedBorderColor,
    double? borderRadius,
  }) {
    return AppTextFieldTheme(
      backgroundColor: backgroundColor ?? this.backgroundColor,
      highLightBackgroundColor:
          highLightBackgroundColor ?? this.highLightBackgroundColor,
      borderColor: borderColor ?? this.borderColor,
      focusedBorderColor: focusedBorderColor ?? this.focusedBorderColor,
    );
  }

  @override
  AppTextFieldTheme lerp(ThemeExtension<AppTextFieldTheme>? other, double t) {
    if (other is! AppTextFieldTheme) return this;
    return AppTextFieldTheme(
      backgroundColor: Color.lerp(backgroundColor, other.backgroundColor, t)!,
      highLightBackgroundColor: Color.lerp(
        highLightBackgroundColor,
        other.highLightBackgroundColor,
        t,
      )!,
      borderColor: Color.lerp(borderColor, other.borderColor, t)!,
      focusedBorderColor: Color.lerp(
        focusedBorderColor,
        other.focusedBorderColor,
        t,
      )!,
    );
  }
}

import 'package:flutter/material.dart';
import 'package:san3a_app/core/helpers/get_text_palette.dart';
import 'package:san3a_app/core/utils/app_text_field_theme.dart';
import 'package:san3a_app/core/utils/app_text_styles.dart';

class CustomUnderLineTextField extends StatelessWidget {
  const CustomUnderLineTextField({super.key, this.controller});

  final TextEditingController? controller;

  @override
  Widget build(BuildContext context) {
    final textPalette = getTextPalette(context);
    final appTextFieldTheme = Theme.of(context).extension<AppTextFieldTheme>()!;

    return TextFormField(
      style: AppTextStyles.getTextStyle(
        20,
      ).copyWith(color: textPalette.headingColor),
      textAlign: TextAlign.center,
      controller: controller,
      decoration: InputDecoration(
        border: UnderlineInputBorder(
          borderSide: BorderSide(
            color: appTextFieldTheme.borderColor,
            width: 1,
          ),
        ),
        enabledBorder: UnderlineInputBorder(
          borderSide: BorderSide(
            color: appTextFieldTheme.borderColor,
            width: 1,
          ),
        ),
        focusedBorder: UnderlineInputBorder(
          borderSide: BorderSide(
            color: appTextFieldTheme.focusedBorderColor,
            width: 2,
          ),
        ),
      ),
    );
  }
}

import 'package:flutter/material.dart';
import 'package:flutter_screenutil/flutter_screenutil.dart';
import 'package:san3a_app/core/utils/app_text_field_theme.dart';

class CustomPasswordTextField extends StatefulWidget {
  const CustomPasswordTextField({
    super.key,
    this.labelText,
    this.hintText,
    this.onChanged,
    this.onSaved,
    this.validator,
    this.controller,
    this.focusNode,
    this.autovalidateMode = AutovalidateMode.disabled,
    this.keyboardType = TextInputType.text,
  });

  final String? labelText;
  final String? hintText;
  final void Function(String)? onChanged;
  final void Function(String?)? onSaved;
  final FormFieldValidator<String>? validator;
  final TextEditingController? controller;
  final FocusNode? focusNode;
  final AutovalidateMode autovalidateMode;
  final TextInputType? keyboardType;

  @override
  State<CustomPasswordTextField> createState() =>
      _CustomPasswordTextFieldState();
}

class _CustomPasswordTextFieldState extends State<CustomPasswordTextField> {
  bool isObscure = true;

  @override
  Widget build(BuildContext context) {
    final appTextFieldTheme = Theme.of(context).extension<AppTextFieldTheme>()!;

    return TextFormField(
      controller: widget.controller,
      focusNode: widget.focusNode,
      obscureText: isObscure,
      onChanged: widget.onChanged,
      onSaved: widget.onSaved,
      validator: widget.validator,
      keyboardType: widget.keyboardType,
      decoration: InputDecoration(
        filled: true,
        fillColor: appTextFieldTheme.backgroundColor,
        labelText: widget.labelText,
        hintText: widget.hintText,
        suffixIcon: IconButton(
          icon: Icon(isObscure ? Icons.visibility : Icons.visibility_off),
          onPressed: () {
            setState(() {
              isObscure = !isObscure;
            });
          },
        ),
        border: OutlineInputBorder(
          borderRadius: BorderRadius.circular(21.r),
          borderSide: BorderSide(
            color: appTextFieldTheme.borderColor,
            width: 1,
          ),
        ),
        enabledBorder: OutlineInputBorder(
          borderRadius: BorderRadius.circular(21.r),
          borderSide: BorderSide(
            color: appTextFieldTheme.borderColor,
            width: 1,
          ),
        ),
        focusedBorder: OutlineInputBorder(
          borderRadius: BorderRadius.circular(21.r),
          borderSide: BorderSide(
            color: appTextFieldTheme.focusedBorderColor,
            width: 2,
          ),
        ),
      ),
    );
  }
}

import 'package:flutter/material.dart';
import 'package:flutter_screenutil/flutter_screenutil.dart';
import 'package:san3a_app/core/helpers/get_text_palette.dart';
import 'package:san3a_app/core/utils/app_text_field_theme.dart';
import 'package:san3a_app/core/utils/app_text_styles.dart';

class CustomTextFormField extends StatelessWidget {
  final String? hintText;
  final String? labelText;
  final String? helperText;
  final Widget? prefixIcon;
  final Widget? suffixIcon;
  final bool? readOnly;
  final TextInputType keyboardType;
  final bool obscureText;
  final String? Function(String?)? validator;
  final TextEditingController? controller;
  final VoidCallback? onSuffixTap;
  final VoidCallback? ontTap;
  final AutovalidateMode? autovalidateMode;
  final int? maxLines;
  final double contentHorizontalPadding;
  final double contentVerticalPadding;
  final FocusNode? focusNode;
  final bool removeBorders;
  final double? labelTextSize;
  final ValueChanged<String>? onChanged;

  const CustomTextFormField({
    super.key,
    this.ontTap,
    this.readOnly,
    this.hintText,
    this.labelText,
    this.helperText,
    this.prefixIcon,
    this.suffixIcon,
    this.keyboardType = TextInputType.text,
    this.obscureText = false,
    this.validator,
    this.controller,
    this.onSuffixTap,
    this.autovalidateMode,
    this.maxLines = 1,
    this.contentHorizontalPadding = 16,
    this.contentVerticalPadding = 14,
    this.focusNode,
    this.removeBorders = false,
    this.labelTextSize = 16,
    this.onChanged,
  });

  @override
  Widget build(BuildContext context) {
    final textPalette = getTextPalette(context);
    final appTextFieldTheme = Theme.of(context).extension<AppTextFieldTheme>()!;

    return TextFormField(
      focusNode: focusNode,
      readOnly: readOnly ?? false,
      controller: controller,
      keyboardType: keyboardType,
      obscureText: obscureText,
      validator: validator,
      onTap: ontTap,
      onTapAlwaysCalled: true,
      autovalidateMode: autovalidateMode,
      maxLines: maxLines,
      onChanged: onChanged,
      style: AppTextStyles.getTextStyle(
        16,
      ).copyWith(color: textPalette.headingColor),
      decoration: InputDecoration(
        filled: true,
        fillColor: appTextFieldTheme.backgroundColor,
        hintText: hintText,
        labelText: labelText,
        helperText: helperText,
        prefixIcon: prefixIcon,
        suffixIcon: suffixIcon != null
            ? GestureDetector(onTap: onSuffixTap, child: suffixIcon)
            : null,
        contentPadding: EdgeInsets.symmetric(
          vertical: contentVerticalPadding.r,
          horizontal: contentHorizontalPadding.r,
        ),
        border: OutlineInputBorder(
          borderRadius: BorderRadius.circular(21.r),
          borderSide: BorderSide(
            color: appTextFieldTheme.borderColor,
            width: 1,
          ),
        ),
        enabledBorder: OutlineInputBorder(
          borderRadius: BorderRadius.circular(21.r),
          borderSide: BorderSide(
            color: appTextFieldTheme.borderColor,
            width: 1,
          ),
        ),
        focusedBorder: OutlineInputBorder(
          borderRadius: BorderRadius.circular(21.r),
          borderSide: BorderSide(
            color: appTextFieldTheme.focusedBorderColor,
            width: 2,
          ),
        ),
      ),
    );
  }
}

