final GlobalKey<NavigatorState> navigatorKey = GlobalKey<NavigatorState>();

Future<void> main() async {
  WidgetsFlutterBinding.ensureInitialized();
  await setupGetIt();
  await EasyLocalization.ensureInitialized();
  await AppStorageHelper.init();
  Bloc.observer = CustomBlocObserver();

  runApp(
    EasyLocalization(
      supportedLocales: [Locale('en'), Locale('ar')],
      path: 'assets/translations',
      fallbackLocale: Locale('en'),
      child: DevicePreview(enabled: true, builder: (context) => MainApp()),
    ),
  );
}

class MainApp extends StatefulWidget {
  const MainApp({super.key});

  @override
  State<MainApp> createState() => _MainAppState();
}

class _MainAppState extends State<MainApp> {
  late AppThemeCubit appThemeCubit;

  @override
  void initState() {
    super.initState();
    appThemeCubit = AppThemeCubit();
  }

  @override
  Widget build(BuildContext context) {
    return MultiBlocProvider(
      providers: [
        BlocProvider(create: (context) => appThemeCubit),
        BlocProvider(
          create: (context) =>
              InternetConnectionCubit(connectivity: getIt<Connectivity>()),
        ),
      ],
      child: BlocBuilder<AppThemeCubit, AppThemeState>(
        builder: (context, state) {
          return ScreenUtilInit(
            designSize: Size(375, 812),
            minTextAdapt: true,
            splitScreenMode: true,
            enableScaleWH: () => false,
            enableScaleText: () => false,
            builder: (context, child) {
              return MaterialApp(
                debugShowCheckedModeBanner: false,
                theme: AppTheme.lightTheme,
                darkTheme: AppTheme.darkTheme,
                themeMode: appThemeCubit.getTheme(),
                navigatorKey: navigatorKey,
                locale: context.locale,
                supportedLocales: context.supportedLocales,
                localizationsDelegates: context.localizationDelegates,
                onGenerateRoute: (settings) =>
                    onGenerateRoutes(settings, context),
                home: const LoginScreen(),
                builder: (context, child) {
                  return DeveloperOverlay(child: child!);
                },
              );
            },
          );
        },
      ),
    );
  }
}

import 'package:flutter/material.dart';
import 'package:flutter_bloc/flutter_bloc.dart';
import 'package:san3a_app/core/cubits/app_theme_cubit/app_theme_cubit.dart';
import 'package:san3a_app/core/enums/app_theme_mode.dart';

class DeveloperOverlay extends StatelessWidget {
  final Widget child;

  const DeveloperOverlay({super.key, required this.child});

  @override
  Widget build(BuildContext context) {
    return Stack(
      children: [
        child,
        Positioned(
          bottom: 50,
          right: 16,
          child: FloatingActionButton(
            mini: true,
            onPressed: () {
              final cubit = context.read<AppThemeCubit>();
              if (cubit.currentTheme == AppThemeMode.light) {
                cubit.changeTheme(AppThemeMode.dark);
              } else {
                cubit.changeTheme(AppThemeMode.light);
              }
            },
            child: const Icon(Icons.brightness_6),
          ),
        ),
      ],
    );
  }
}

